<script type="text/javascript">	$(document).ready(function(){		$("article .entry-content p").find("iframe").parent("p").css("text-align","center");	});</script><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>	<header class="entry-header">		<?php if ( is_single() ) : ?>			<h1 class="entry-title"><?php the_title(); ?></h1>		<?php else : ?>			<h1 class="entry-title">				<a href="<?php the_permalink(); ?>" rel="bookmark"><?php the_title(); ?></a>			</h1>		<?php endif; ?>		<div class="meta-top1">			<div class="meta-top2">				<?php hmjblog_entry_meta(); ?>				<?php if ( is_singular() && get_the_author_meta( 'description' ) && is_multi_author() ) : ?>					<div class="author-info">						<div class="author-avatar">							<?php							$author_bio_avatar_size = apply_filters( 'hmjblog_author_bio_avatar_size', 68 );							echo get_avatar( get_the_author_meta( 'user_email' ), $author_bio_avatar_size );							?>						</div>						<div class="author-description">							<h2><?php printf( __( 'About %s', 'hmjblog' ), get_the_author() ); ?></h2>							<p><?php the_author_meta( 'description' ); ?></p>							<div class="author-link">								<a href="<?php echo esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ); ?>" rel="author">									<?php printf( __( '显示作者所有文章 %s <span class="meta-nav">&rarr;</span>', 'hmjblog' ), get_the_author() ); ?>								</a>							</div>						</div>					</div>				<?php endif; ?>			</div>			<?php if ( comments_open() ) : ?>				<div class="comments-link">					<i class="fa fa-comments"></i><?php comments_popup_link('<span class="leave-reply">'.__('暂无评论','hmjblog').'</span>',__('1条评论','hmjblog' ), __( '%条评论', 'hmjblog')); ?>　<i class="fa fa-eye"></i><?php post_views('', '次浏览'); ?>				</div>			<?php endif; ?>			<?php if ( !comments_open()) : ?>				<div class="comments-link">					<i class="fa fa-comments"></i><span class="leave-reply">评论关闭</span>　<i class="fa fa-eye"></i><?php post_views('', '次浏览'); ?>				</div>			<?php endif; ?>		</div>	</header>	<div class="post-thumbnail">		<a href="<?php echo get_permalink(); ?>" title="<?php the_title(); ?>"><?php if (  ! post_password_required() && ! is_attachment() && !is_single() ) :				the_post_thumbnail();			endif; ?></a>	</div>	<?php if ( !is_single() ) : ?>		<div class="entry-summary">			<?php the_excerpt() ?>		</div>	<?php else : ?>		<div class="entry-content">			<?php the_content( __( '阅读全文 <span class="meta-nav">&rarr;</span>', 'hmjblog' ) ); ?>			<?php wp_link_pages( array( 'before' => '<div class="page-links">' . __( 'Pages:', 'hmjblog' ), 'after' => '</div>' ) ); ?>			<div class="post-link-share">				<!--				<div class="post-like">-->				<!--					<a href="javascript:;" data-action="ding" data-id="--><?php //the_ID(); ?><!--" class="specsZan --><?php //if(isset($_COOKIE['specs_zan_'.$post->ID])) echo 'done';?><!--"><i class="fa fa-thumbs-o-up"></i>赞 <span class="count">-->				<!--        --><?php //if( get_post_meta($post->ID,'specs_zan',true) ){				//			echo get_post_meta($post->ID,'specs_zan',true);				//		} else {				//			echo '0';				//		}?><!--</span>-->				<!--					</a>-->				<!--				</div>-->				<div class="bshare-custom icon-medium-plus">					<div class="bsPromo bsPromo2"></div>					分享到：					<a title="分享到微信" class="bshare-weixin" href="javascript:void(0);"></a>					<a title="分享到新浪微博" class="bshare-sinaminiblog" href="javascript:void(0);"></a>					<a title="分享到QQ空间" class="bshare-qzone"></a>					<a title="分享到Facebook" class="bshare-facebook" href="javascript:void(0);"></a>					<a title="分享到Twitter" class="bshare-twitter" href="javascript:void(0);"></a>					<a title="分享到腾讯微博" class="bshare-qqmb" href="javascript:void(0);"></a>					<a title="更多平台" class="bshare-more bshare-more-icon more-style-addthis"></a>				</div>				<script type="text/javascript" charset="utf-8" src="http://static.bshare.cn/b/buttonLite.js#style=-1&amp;uuid=93d31093-24b9-4ae0-b24a-4865c8f96738&amp;pophcol=2&amp;lang=zh"></script><script type="text/javascript" charset="utf-8" src="http://static.bshare.cn/b/bshareC0.js"></script>			</div>		</div>	<?php endif; ?></article>